google.load("visualization","1",{packages:["corechart"]});google.setOnLoadCallback(chart_this);function chart_this(){$.getJSON("js/employees.json",function(c){employee_data=c;array_data=[];array_data.push(["Department","Employees"]);sorted_for_total=_.sortBy(employee_data,function(d){return d.employees}).reverse();$.each(sorted_for_total,function(d,e){array_data.push([e.short_name,e.employees])});var c=google.visualization.arrayToDataTable(array_data);var a={title:"Total Employees (Logarithmic Scale)",vAxis:{title:"Department",titleTextStyle:{color:"black"}},height:800,hAxis:{logScale:true},colors:["slategray"]};var b=new google.visualization.BarChart(document.getElementById("employees_chart"));b.draw(c,a);$('[data-spy="scroll"]').each(function(){var d=$(this).scrollspy("refresh")})})};